import request from '@/utils/request'

// 是否 可注册
export function checkPhone(data) {
  return request({
    url: '/user/visit/checkPhone',
    method: 'post',
    data
  })
}

// 发送短信
export function sendSmsVerificationCode(data) {
  return request({
    url: '/user/visit/sendSmsVerificationCode',
    method: 'post',
    data
  })
}

// 验证 短信验证码是否正确
export function checkPhoneVerifyCode(data) {
  return request({
    url: '/user/visit/checkPhoneVerifyCode',
    method: 'post',
    data
  })
}

// 注册
export function register(data) {
  return request({
    url: '/user/visit/register',
    method: 'post',
    data
  })
}

// 登录
export function login(data) {
  return request({
    url: '/user/visit/login',
    method: 'post',
    data
  })
}

// 退出
export function logout(data) {
  return request({
    url: '/user/visit/logout',
    method: 'post',
    data
  })
}

// 重置密码
export function resetPassword(data) {
  return request({
    url: '/user/visit/resetPassword',
    method: 'post',
    data
  })
}

// 修改密码
export function updatePassword(data) {
  return request({
    url: '/user/visit/updatePassword',
    method: 'post',
    data
  })
}

// 获取 人机验证 KEY
export function getSiteKey() {
  return request({
    url: '/user/visit/getSiteKey',
    method: 'get'
  })
}

// 获取 图片验证码
export function getCode() {
  return request({
    url: '/user/visit/getCode',
    method: 'get',
    responseType: 'arraybuffer'
  })
}

